mapper.update:
update
tp_case_product


    product_name=#{productName}


    productman_id=#{productmanId}


    status=#{status}

  modified_time=now()

    modifier_id = #{modifierId}


>
where id=#{productId}

mapper.select:
select count(1)
from tp_case_product p
inner join (select id,name from t_platform_user) u
on p.productman_id = u.id


    and p.product_name = #{productName}

   and p.status = #{status} 
   and u.name = #{productman}

select p.id,p.product_name,p.status,u.productman
from tp_case_product p
inner join (select id,`name` productman from t_platform_user ) u
on p.productman_id = u.id

  and p.id = #{id} 
  and u.productman_id = #{productmanId}
  and p.status = #{status} 

order by p.product_name

  limit #{offset},#{limit}
